do {                                                                                             \
  ensure_gpio14_initialized();                                                                   \
  if (define_name) {                                                                             \
    RUN_TEST_IN_TASK(test_name, test_func, stack_size, priority);                                \
    flip_test_progress_indicator();                                                              \
  } else {                                                                                       \
    ESP_LOGI(TAG, "Test '%s' disabled by configuration", test_name);                             \
  }                                                                                              \
} while (0)
